INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('73', '7', '订单何时出库？', '<p>&nbsp;<span style=\"color: rgb(102, 102, 102); font-family: Arial; line-height: 21px;\">订单的出库时间要以您订单的配货情况而定。请您随时登录&ldquo;会员中心&rdquo;查看订单状态。如果订单显示&ldquo;已发货&rdquo;，说明订单已经出库，请您耐心等待收货。</span></p>', '', '', '', '0', '1', '1370453269', '', '0', 'http://116.255.154.183/article.php?id=77', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('74', '7', '送货与验货', '<p style=\"\">&nbsp;</p>\r\n<div class=\"pageWrap\" style=\"margin: 0px; padding: 0px; border: 0px; font-family: Arial; vertical-align: baseline; list-style: none; color: rgb(102, 102, 102); line-height: 21px;\">\r\n<h2 style=\"margin: 15px 0px; padding: 0px; border: 0px; font-style: inherit; font-size: 16px; font-family: inherit; vertical-align: baseline; list-style: none;\">签收商品时需要注意哪些问题？</h2>\r\n<p>1、送货上门、货到付款订单：快递员送货上门时，请您务必当面对照发货单核对商品，如果出现商品数量缺少、商品破损，请您当场办理整单商品的退货。若订单中含有赠品，请一并退回。一旦您确认签字，我们将无法为您办理退换或补发。</p>\r\n<ol style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-family: inherit; vertical-align: baseline; list-style: none;\">\r\n    <p style=\"\"><strong>特别提示：</strong></p>\r\n    <li style=\"margin: 0px 0px 0px 20px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: solid; border-bottom-color: rgb(239, 239, 239); font-style: inherit; font-family: inherit; vertical-align: baseline; list-style: decimal; line-height: 20px;\">如果您的订单使用帐户余额或礼券支付，只有退货商品的金额小于实际应付款金额时才可办理。</li>\r\n    <li style=\"margin: 0px 0px 0px 20px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: solid; border-bottom-color: rgb(239, 239, 239); font-style: inherit; font-family: inherit; vertical-align: baseline; list-style: decimal; line-height: 20px;\">如果您的订单中含有赠品，将无法提供此项服务；如果是成套商品，您只能整套退货。如果是捆绑商品，您在退主商品的同时需要将赠品一起退回。</li>\r\n</ol>\r\n<p style=\"margin: 0px 0px 1.5em; padding: 0px; border: 0px; font-style: inherit; font-family: inherit; vertical-align: baseline; list-style: none;\">2、邮局邮寄订单：请您在领取包裹时务必检查外包装，如果发现包裹破损，请您不要签收，随后请及时将包裹单原件邮寄给我们，您的包裹单原件将作为我们为您办理补发或退款的唯一证明。收到包裹单后，我们将为您办理相关手续。如您未拆开外包装箱，也可以当场全部退货。平邮订单，在收到包裹时，如发现包裹破损，请您要求邮局出具包裹破损证明。</p>\r\n</div>\r\n<p style=\"margin: 0px 0px 1.5em; padding: 0px; border: 0px; font-family: Arial; vertical-align: baseline; list-style: none; color: rgb(102, 102, 102); line-height: 21px;\">&nbsp;</p>', '', '', '', '0', '1', '1370453317', '', '0', 'http://116.255.154.183/article.php?id=76', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('67', '7', '货到付款', '<p>&nbsp;<strong style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; line-height: 20px; padding: 0px; margin: 0px;\">现金支付</strong><span style=\"color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; line-height: 20px;\">：货物送到订单指定的收货地址后，由收货人支付货款给送货人员</span></p>\r\n<p><dd style=\"padding: 0px; margin: 0px 0px 10px; list-style: none; color: rgb(51, 51, 51); font-family: Arial, Helvetica, sans-serif; line-height: 20px;\"><strong style=\"padding: 0px; margin: 0px;\">POS机刷卡支付</strong>：货物送到订单指定的收货地址后，由收货人刷卡支付订单金额</dd></p>\r\n<p>&nbsp;</p>', '', '', '', '0', '1', '1370410413', '', '0', 'http://116.255.154.183/article.php?id=83', '');
