INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('96', '13', '完整的健康，从“清调补”开始', '<p>&nbsp;</p>\r\n<h1><span style=\"background-color: rgb(255, 255, 255);\"><br type=\"_moz\" />\r\n</span></h1>\r\n<h1 style=\"text-align: center;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(55, 133, 80); font-family: 微软雅黑, 黑体; font-size: 18px; line-height: 20px;\"><span style=\"font-size: larger;\">完整的健康，从&ldquo;清调补&rdquo;开始</span></span></span></h1>\r\n<p><span style=\"background-color: rgb(255, 255, 255);\"><br type=\"_moz\" />\r\n</span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>健康的重要性:</strong></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">健康是基于财富、自由、幸福、快乐之上的。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">有了健康你才能享有辉煌的事业、无限的钱财、享受着人生。没有了健康你生活的一切还存在吗？</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">没有健康就没有一切。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>健康与长寿：</strong></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">人类的预期寿命应是生长期的5-6倍，人类的生长期约为25年（人生长的最后一颗牙齿计算），由此推算，人类的预期寿命可达到125-150岁。那我们现在的人均寿命只有73。5岁。为什么我们不能活到自然寿命呢？因为预期寿命的获得要以健康为基础。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>健康的标准：</strong></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">什么是健康呢？健康是指身体上、精神上和社会适应能力上的完好状态，而不仅仅是身体没有疾病。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">根据WHO的健康标：</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">1。精力良沛；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">2。善于休息，睡眠良好；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">3。适应、应变能力强；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">4。对疾病有较好的抵抗力；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">5。体重适当，身体匀称；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">6。乐观积极；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">7。眼睛明亮，反应敏锐；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">8。牙龄清洁，牙龈健康；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">9。头发光泽，无头屑；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">10。肌肉、皮肤有弹性，走路轻松。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">简单的说就是五快：吃的快、睡的快、走的快、说的快、排的快，能达到这五快十点的人，才算健康。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">世界卫生组织把人分为三类：</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 健康的人群约占总人类的&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;5%</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 亚健康的人群约占总人类的&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">75%</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 疾病的人群约占总人类的&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;20%</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">健康的人基本是婴儿及宙行员等人群；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">疾病的就是那些在疾病里需要救治的人群；</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">亚健康是介于健康与疾病之间的一种游离状态，它有双向性，如果保健得当，有希望回到健康状态，要是任其发展就会导致疾病的发生。睡眠不好、易做噩梦、疲劳、情绪低落、易怒、烦躁、注意力不集中、记忆力减退等这些全部是亚健康的表现。许多疾病在早期的先兆都表现在亚健康状态中，所以大家要重视亚健康状态。亚健康简单的说就是&ldquo;身体不舒服，到医院又查不出毛病&rdquo;。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">处于这三种状态的人要如何获得健康呢？我们要筑起三道防线，全面维护健康。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">第一道防线：预防</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">第二道防线：保健</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">第三道防线：治疗</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>预防：</strong></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">科学的饮食、适量的运动、充分的休息、食好的个人习惯、优食的环境和能保持心态的平衡这六个因素都完善那我们说这个人是健康的。可是这六个因素中有很多都不是我们能控制的。比如：适量的运动，现在在运动场上看到的都是老年人多。大多数上班族都没有时间锻炼；食良的环境：现在每天我们都能听到空气质量报告，什么可吸入颗粒物，二氧化硫等。所以第一道防线很容易被疾病攻破。日积月累使我们体内堆积了很多毒素，这些毒素使各系统代谢失调，我们又没有科学的饮食使营养失衡，身体就滑向亚健康状态。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>保健：</strong></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">需要&ldquo;清、调、补&rdquo;，清：清除体内毒素；调：调节身体各项机能；补：补充身体内所需要的均衡营养。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>治疗：</strong></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">当身体不健康因素积累到一定程度时就会造成疾病，就需要医治。我们充分肯定药物与手的术的作用，但尽量要远离它，自己的健康寄托在治疗手段上是不科学的。最好的医生就是自己，当你得了病以后。再好的医生也不能把你的身体恢复到你得病以前的状态。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">我们坚信建立在健康基础上的财富、地位、幸福及快乐才能长久，我们远离药物而完整的<font style=\"word-wrap: normal; word-break: normal; line-height: 36px; font-size: 24px;\">健康就必须&ldquo;清调补&rdquo;。</font></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>清&mdash;&mdash;清毒养颜&ldquo;高纤乐&rdquo;&ldquo;高纤餐&rdquo;</strong></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">俗话说&ldquo;要想长生，肠中常清&rdquo;大肠就像人体的垃圾桶，在胃和小肠内未被消化的食物残留物和人体废物，都要由大肠受纳后形成大便排出体外。大肠是一根与身高几乎同长，里面充满褶皱的管道。在长年累月地运输废物残渣的过程中，大肠又长期得不到清洗，时间长了，这些垃圾越积越厚，久而久之就形成了&ldquo;黑色的、带状的含有各种废物的宿便&rdquo;。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">由于现在人们的生活方式和饮食习惯发生了很大变化，现代人趋向于精细、色美、高营养结构，而忽略了对膳食纤维的要求（世界卫生组织要求：正常人每天所需纤维量为25-40克，约4公斤菠菜的含量，可是我们现在每天只有10克左右的）摄入量不足，使便量减少，不能产生便意及时排除体外。导致粪便在大肠内长期滞留，造成便秘。我们的体温是37度，当宿便在体内发酵或发生其他化学变化时，就会产生36种毒素，导致179种疾病（其中有22种是恶性的不可逆转的）。毒素长期积累在体内，会导致人体消化系统的疾病，如胃炎、肝硬化、肠癌等等。其中最突出的是&ldquo;大肠肿瘤&rdquo;！宿便使大肠壁长期缺氧，导致细胞癌变，形成肿癌。世界上患大肠肿瘤的人数每年增长5%，由宿便引起的大肠疾病成为人类的&ldquo;第一号杀手&rdquo;！</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">可见宿便对我们人体的健康有很大的危害。如何清除大肠宿便，治疗便秘呢？目前有三种方法。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>第一种就是机械治疗：</strong>通过水对大肠进行冲洗，加快结肠蠕动来排出废物。这种方法价格高，过程痛苦不说，而且在把肠内的有害菌洗掉的财时，有益菌也一起被清洗出去了，会引起菌群失调。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>第二种是药物治疗：</strong>就是通过吃果导片、麻仁润肠等泻药产生便意。一旦停药，便秘会更加严重，形成恶性循环。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>第三种是膳食疗法：</strong>主要是指摄入适量的膳食纤维，能够刺激肠道蠕动，便食物残渣更快的进入肠道，减少小肠对毒素及致癌物质的吸收，清洁肠道。膳食纤维在小肠内不被吸收，在结肠中可以破裂分解，像一个海绵团吸水膨胀，使废物变软，迅速滑脱出体外。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>完美高纤乐就是通过膳食疗法来清除宿便，治疗便秘的。</strong></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">高纤乐的主要成份：西莲壳、沙棘和草莓</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">西莲壳：完美公司采用的西莲壳为全世界品质最好的，产地印度。它是目前世界上为人类所知道的食物中膳食纤维含量最高的植物，被称为&ldquo;纤维巨人&rdquo;。每单位西莲壳中含有86%的膳食纤维，其中可溶性纤维占71%，相当于燕麦的14倍。可溶性纤维进入人体血液，达到高质量清血的效果。可以降低血液中的胆固醇的含量，对预防高胆固醉症，心脑血管疾病等有重要作用。15%不可溶性，直接进入大肠及肺部。清除肺部垃圾和肠道中垃圾。西莲壳除了可以促进肠胃蠕动，缩短食物残渣经过肠道的时间，减少有毒物质对结肠的刺激外，还可以清除肺部及血液垃圾是人体的&ldquo;清道夫&rdquo;</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">沙棘：是一种药食同源的植物，被称为高原圣果，维生素之王。蒙医、藏医都用它来制药。《中国药典》中记载：沙棘有活血、祛瘀、养胃健脾、抗炎生肌的药理功效。沙棘中含有丰富的不饱和脂肪酸及190余种生物活性物质。沙棘还被称为&ldquo;维生素宝库&rdquo;，维生素C的含量是猕猴桃的8倍，鲜橙的24倍，甚至是葡萄的200倍。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">草莓：它被称为&ldquo;水果之王&rdquo;，是药食两用的植物，中国医学史料中记载：草莓具有清热、解暑、止渴、利尿、润喉等功效，同时还能清洁肠胃道，保护肝脏。草莓中含有柔华酸，具有抗癌防癌的功效。草莓中含有丰富的维生素、有机酸、钙、磷等多种营养，也是祛皱增白，美容之理想食品。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">一包10克的高纤乐含有的膳食纤维相当于我们每天吃四公斤菠菜的纤维量。使用完美高纤乐可以将长期滞留在肠道内的废弃物在自然情况下排出体外，使您无毒一身轻。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong>调&mdash;&mdash;调理身体机能&ldquo;芦荟矿物晶&rdquo;</strong></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">芦荟矿物晶的主要成分：65%的芦荟精华毒、油菜花粉、蜂皇浆、Vc、果糖、74种微量元素。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">芦荟：被称为21世纪最具开发潜秘的植物新资源，它生长在热带及亚热带地区，芦荟有四百多个品种，其中分为美容芦荟、食用芦荟、观赏芦荟、药用芦荟。在这四百多个品种中有药用价值的只有6-7种，其中首选的是美国&ldquo;库拉索&rdquo;芦荟。美国&ldquo;库拉索&rdquo;芦荟是集美容、食用、观赏、药用为一体的高品质芦荟。完美芦荟矿物晶中采用的芦荟就是生长期在3年以上的美国库拉索芦荟。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">在中国药典中把芦荟称为&ldquo;象胆&rdquo;，它能够清热、解毒、明目、通便、治疗疥癣等。另外，它还能抗菌消炎，清血排毒、美容养颜、修复再生、改善消化、调节免疫、抗肿瘤降血糖。因此芦荟是来自大自然的食药。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">花粉：采用高科技&ldquo;风冷破壁&rdquo;技术。去除花粉坚硬的外壳及过敏源，同时不破坏其营养成份，使人体可以100%的吸收营养并无过敏。所以对花粉过敏者请放心食用。绝无不良反应。能够促进新陈代谢、调节免疫力、消除自由基、抑制前列腺增生、促进血液循环、改善脑功能、预防心血管系统疾病。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">蜂皇浆：可以达到增强体制、延缓衰老、调节内分泌、调节神系统、健脑益智、抗肿瘤、促进消化等作用。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">Vc：它可以维持血管弹性，保护心脏，人体不能自身合成Vc，只能在水果蔬菜中才能摄取的到，并且它只能在体内存活2-4小时，所以要随时补充。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">果糖：又叫单分子糖，不用经过胰岛素分解，直接通过血液吸收。糖尿病人放心食用。它可以改善头晕、乏力等现象。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">74种微量元素：也就是矿物质，它对人体造血、骨骼生长、食物分解、消化吸收以及细胞的新陈代谢都起着至关重要的作用。矿物质需要量很少，但绝对不可缺少。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><font style=\"word-wrap: normal; word-break: normal; line-height: 27px; font-size: 18px;\"><strong>芦荟矿物晶具有三大功效：</strong></font></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">一、排毒解毒</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">由于空气的污染以及食物中的农药化肥的残留物进入人体后，使人体内的细胞受到破坏，日积月累生理机能出现代谢障碍。芦荟矿物晶能有效地清除积存在体内的废物和毒素，分解体内的有害物质，迅速排出体外。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">二、调理人体五大系统</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 呼吸系统：芦荟矿物晶能促进呼吸系统肌内和粘膜组织再生，减缓哮喘病的发作。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 消化系统：芦荟矿物晶能促进胃液正常分泌，促进消化系统正常运转。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 血液循环系统：芦荟矿物晶有清血的作用，能够促进血液循环，防止心脑血管病的发生。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 内分泌系统：人体的肝胆肾都要正常分泌荷尔蒙和激素来维持人体的正常代谢，内必泌系统异常就会造成内分泌障碍。糖尿病人就是因为糖代谢障碍造成的。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 免疫系统：免疫系统有三种功能：1。清除体内异常细胞、修复受损的细胞；2。清除衰老的细胞；3。抵御外来病毒和病菌侵入。饮食不当，营养不均衡，环境污染产生的毒素都可以直接使免疫功能受损。芦荟矿物晶中的芦荟、Vc、花粉等营养可增加我们的免疫力。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">三、补充人体多种矿物质</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">1996年，在广州召开了微量元素与健康的研讨会，到会的600多位专家学者向人们警告，当今人类疾病90%与微量元素有关。人类的健康受到严重威胁。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">医学专家研究指出：人体对矿物质的要求，不但种类多，而且要求各种矿物质之间有一定的比例。我们现在都知道中国人缺钙，市面上补钙的产品也很多，但是很少人知道，钙的吸收要有镁，磷和维生素D的配合才能被人体吸收。微量元素像生物链一样相辅相成，缺少某种矿物质就会引起某种疾病，如：</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 钙：老人缺钙会造成骨质疏松，儿童缺钙会引起佝瘘病，发育不良。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 锌：它被称为&ldquo;生命之花&rdquo;，如果小孩缺锌会影响智力发育，造成多运症；另外锌在眼睛中的含量最多，如果严重缺乏会造成白内障，成年人缺锌会造成不孕不育症。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 铁：缺铁会造成贫血，但是铁要有铜的配合才能发挥作用。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 铬：它能帮助维持体内正常的血糖代谢，如果严重缺铬5年以上会引起糖尿病。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 碘：缺碘会造成甲状腺分泌失调，出现大脖子病，严重缺碘孕妇会产下痴呆儿。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 硒：是&ldquo;抗癌明星&rdquo;，缺硒会导致癌症。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 镁：能镇静神经，缺镁易患神经性头疼。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">矿物质犹如催化剂，对每一个有生命的细胞都产生着至关重要的作用。芦荟矿物晶能够全面地补充人体所需要的各种矿物质，帮助肌体正常运转。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><strong><font style=\"word-wrap: normal; word-break: normal; line-height: 27px; font-size: 18px;\">芦荟矿物晶在生产过程中的五大高科技</font></strong></span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* &ldquo;低温冷冻稳定&rdquo;提炼法：芦荟虽然具有很高的医疗价值，但却很容易腐坏，不易收存，经过国际间的不断努力，研究出了这种提炼法，使芦荟的酵素活性和疗效得以完整的保存。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* &ldquo;风冷破壁&rdquo;技术：由于花粉有层坚硬的外壳而且耐腐蚀，就像葵花子一样，不能吸收它的营养成分。而&ldquo;风冷破壁&rdquo;技术就是去除掉花粉坚硬的外壳，同时不破坏掉其营养成份，使我们可以100%的吸收到营养。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 1：200倍的高农缩结晶粉状</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 多种矿物质的均衡配比</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">* 包装采用纸浆高密度加压技术，绿色环保。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">补&mdash;&mdash;补充均衡营养&ldquo;肽藻粉&rdquo;&ldquo;营养餐&rdquo;</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">随着生活水平的提高，人们对膳食条件的要求也逐渐提高。但是，要知道吃的好不一定吃的科学。中国营养学会指出：当今中国居民由于膳食结构的不合理，造成了现代文明病的产生。并强调指出要全面补充均衡的营养。人体均衡营养可分为七大类：蛋白质、脂肪碳水化合物、维生素、矿物质、纤维素和水。其中，蛋白质、脂肪、碳水化合物是提供能量的。维生素、矿物质是转化能量的，纤维素、水是代谢废物的。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">中国营养学会为中国居民提供了正确的膳食结构&mdash;&mdash;中国居民均衡&ldquo;膳食宝塔&rdquo;</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">盐0.6克&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;油 0.25克</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;豆奶类 0.4斤</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;鱼肉蛋类&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;0.5斤</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;蔬菜水果类&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">0.8-1.2斤</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;五谷杂粮类&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;&nbsp;</span></span><wbr></wbr><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">0.8-1斤</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">可我们现在的饮食几乎与之相反，从饮食结构可以看出蛋白质、脂肪、碳水化合物一般都不会缺乏，而脂肪、碳水化合物摄取过多会引起脂肪的堆积，影响健康。最容易缺乏矿物质、维生素。营养的严重失衡正是导致现代&ldquo;文明病&rdquo;、&ldquo;富贵病&rdquo;的根源。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">肽藻粉的主要成份：大豆低聚肽、乳清蛋白、螺旋藻、糙米、南瓜粉、黑米、薏仁、莲子等等。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">大豆低聚肽是将大豆蛋白通过酶解或酸水解的方法制备的小分子肽类物质，主要是由3-6个氨基酸构成，分子量在1000以下。分子量小可以不经过肠胃等功能转化直接被血液等部位直接吸收。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">大豆低聚肽的理化特性：1。分子量小；2。水溶性好；3。热稳定性好；4。酸稳定性好；5。直接吸收性好；6。安全性。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">大豆氏聚肽的生理功能：1。改善小肠组织结构和吸收功能；2。增强机体的免疫力；3。降血压；4。血脂调节作用；5。促进脂肪代谢；6。抗氧化；7。抗运动疲劳等。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">乳清蛋白：乳清蛋白被称为蛋白之王，是从牛奶中提取的一种蛋白质，具有营养价值高、易消化吸收含有多种活性成分等特点。是公认的人体优质蛋白质补充剂之一。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">螺旋藻：是一低等植物，属蓝藻门颤藻科。螺旋藻内含有大量的天然蛋白，其特有的藻蓝蛋白，能够提高淋巴细胞活性，增强人体免疫力，因此对胃肠疾病及肝病患者康复具有重要意义。其中维生素及矿物质含量极为丰富，包括多种维生素及矿物质。螺旋藻含有在量的r-亚麻酸，这是一种人类必需的不饱和脂肪酸。是健脑益智、清除血脂、调节血压、降低胆固醇的理想物质。螺旋藻中的螺旋藻多糖具有抗辐射损伤和改变放化疗引起的副反应作用的。因此对肿瘤患者是食疗佳品。螺旋藻中叶绿素含量极为丰富。是普通蔬菜量的10倍以上。对促进人体消化和血液中的毒素及改善过敏体质，消除内脏炎症等都有积极作用。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">糙米：每一粒稻米去掉稻壳后就是糙米。糙米由糠层、胚芽和胚乳三部分构成，从体积上看胚乳占92%。糠层占5%，胚芽占3%。但从营养成分来看，却正好相反，也就是说只占3%的胚芽其中的营养成分占一颗糙米的70%。由此可知糙米胚芽的营养价值非常高。胚芽中的膳食纤维是精白米的4倍，而胚乳主要是碳水化合物，其他营养素如维生素和矿物质的含量几乎为0。我们每天吃的大米是去掉胚芽和糠层只剩下胚乳的精白米，营养成分非常少。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">薏仁：健脾利湿、清热排毒。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">莲子：补脾胃、益肾固精、补养心气。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">肽藻粉在加工过程中采用了超微破碎、糊化、低温干燥等先进的处理方法，保证营养成分不流失、不被破坏，极易被人体所消化吸收。使肽藻粉食用起来香而不燥，不用担心会上火。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">清调补秉承了古人&ldquo;不治已病治未病&rdquo;&ldquo;预防胜于治疗&rdquo;的思想。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">早在几千年前《皇帝内经》中就提出&ldquo;清调补&rdquo;的中医理论。完美公司所出品此套功能型保健食品就是集清调补与一体的人间极品。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">今天100元的预防，远胜于将来800元的治疗，10000元的抢救。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">建立在健康基础上的财富、地位、快乐才能持久，而完整的健康必须&ldquo;清、调、补&rdquo;。</span></span></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\">&nbsp;</span></span><wbr></wbr></p>\r\n<p style=\"margin: 0px 0px 5px; padding: 0px; border: 0px; list-style: none; word-wrap: normal; word-break: normal; line-height: 21px; color: rgb(73, 73, 73); font-family: simsun; font-size: 14px;\"><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(51, 153, 102);\"><font style=\"word-wrap: normal; word-break: normal; line-height: 36px; font-size: 24px;\">祝您健康！</font></span></span></p>', '', '', '', '0', '1', '1371089745', '', '0', 'http://', '');
